mapper.insert:
INSERT INTO user (id
    , password
    , name
    , authority
    , phone_number
    , email
    , corp_id)
VALUES (#{id}
    , #{password}
    , #{name}
    , #{authority}
    , #{phoneNumber}
    , #{email}
    , #{corpId})

mapper.update:
    	UPDATE user
		SET email=#{email}, phone_number=#{phoneNumber}
		WHERE id=#{id};
